mapper.select:
select 

from ppf_customer_fee_month_detail
where CUSTOMERID = #{customerid,jdbcType=INTEGER}
  and DATATIME = #{datatime,jdbcType=TIMESTAMP}
  and POINTID = #{pointid,jdbcType=DECIMAL}
  and STARTTIME = #{starttime,jdbcType=TIMESTAMP}

mapper.delete:
delete from ppf_customer_fee_month_detail
where CUSTOMERID = #{customerid,jdbcType=INTEGER}
  and DATATIME = #{datatime,jdbcType=TIMESTAMP}
  and POINTID = #{pointid,jdbcType=DECIMAL}
  and STARTTIME = #{starttime,jdbcType=TIMESTAMP}
